<?php

namespace LampSBTCPay;

use Shopware\Components\Plugin;
use Shopware\Components\Plugin\Context\ActivateContext;
use Shopware\Components\Plugin\Context\DeactivateContext;
use Shopware\Components\Plugin\Context\InstallContext;
use Shopware\Components\Plugin\Context\UninstallContext;
use Shopware\Models\Payment\Payment;

class LampSBTCPay extends Plugin
{
    /**
     * @param InstallContext $context
     */
    public function install(InstallContext $context)
    {
        /** @var \Shopware\Components\Plugin\PaymentInstaller $installer */
        $installer = $this->container->get('shopware.plugin_payment_installer');

        $options = [
            'name' => 'btcpay_payment',
            'description' => 'Kryptow채hrungen',
            'action' => 'CryptoGatePayment',
            'active' => 0,
            'position' => 0,
            'additionalDescription' =>
                '<img src="data:image/png;base64,"/>'
                . '<div id="payment_desc">'
                . '  Jetzt sicher und verschl체sselt mit Bitcoin (Lightning) zahlen.'
                . '</div>'
        ];
        $installer->createOrUpdate($context->getPlugin(), $options);

        $options = [
            'name' => 'btcpay_payment_lbtc',
            'description' => 'Kryptow채hrung Bitcoin Lightning',
            'action' => 'BTCPayPayment',
            'active' => 0,
            'position' => 0,
            'additionalDescription' =>
                '<img src="data:image/png;base64,"/>'
                . '<div id="payment_desc">'
                . '  Jetzt sicher und verschl체sselt mit Bitcoin Lightning zahlen.'
                . '</div>'
        ];
        $installer->createOrUpdate($context->getPlugin(), $options);

        $shop          = Shopware()->Models()->getRepository('Shopware\Models\Shop\Shop')->findOneBy(array('default' => true));
        $pluginManager = Shopware()->Container()->get('shopware_plugininstaller.plugin_manager');
        $plugin        = $pluginManager->getPluginByName('LampSBTCPay');
        $pluginManager->saveConfigElement($plugin, 'SECRET', sha1(uniqid()), $shop);

    }

    /**
     * @param UninstallContext $context
     */
    public function uninstall(UninstallContext $context)
    {
        $this->setActiveFlag($context->getPlugin()->getPayments(), false);
    }

    /**
     * @param DeactivateContext $context
     */
    public function deactivate(DeactivateContext $context)
    {
        $this->setActiveFlag($context->getPlugin()->getPayments(), false);
    }

    /**
     * @param ActivateContext $context
     */
    public function activate(ActivateContext $context)
    {
        $this->setActiveFlag($context->getPlugin()->getPayments(), true);
    }

    /**
     * @param Payment[] $payments
     * @param $active bool
     */
    private function setActiveFlag($payments, $active)
    {
        $em = $this->container->get('models');

        foreach ($payments as $payment) {
            $payment->setActive($active);
        }
        $em->flush();
    }

    /**
     * @return string
     */
    public function onGetBackendController()
    {
        return __DIR__ . '/Controllers/Backend/BTCPayPairing.php';
    }

    /**
     * @return array
     */
    public static function getSubscribedEvents()
    {
        return [
            'Enlight_Controller_Dispatcher_ControllerPath_Backend_BTCPayPairing' => 'onGetBackendController'
        ];
    }
}
